mapper.select:


		select * from(  		
			select rownum as rn, replyId, clothId, memberId, content, rating, regDate
			from mus_reply
			where rownum <= #{pageNum} * #{amount}  and clothId = #{clothId}	
			order by regDate desc  	
  			)
  		where rn > (#{pageNum} -1) * #{amount}

select count(*)
from mus_reply
where clothId = #{clothId}
